void drawCenterString(const String &buff, uint16_t y = 31, byte size = 1) {
    int16_t x1, y1;
    uint16_t w, h;
    oled.setTextSize(size);
    oled.getTextBounds(buff, 0, 0, &x1, &y1, &w, &h);
    oled.setCursor(63 - w / 2, y);
    oled.println(buff);
}
