void drawBarChartH(bool &Redraw, float &curval, double x = 20, double y = 60, double w = 80, double h = 25, double loval = -40, double hival = 80, double inc = 20, double dig = 4, String label = "Temp") {
    double stepval, mx, level, i, data;

    if (Redraw) {
        Redraw = false;
        oled.fillRect(0, 0, 127, 16, SSD1306_WHITE);
        oled.setTextColor(SSD1306_BLACK, SSD1306_WHITE);
        oled.setTextSize(1);
        oled.setCursor(3, 62);
        oled.println(label);
        // step val basically scales the hival and low val to the width
        stepval = inc * (double(w) / (double(hival - loval))) - .00001;
        // draw the text
        for (i = 0; i <= w; i += stepval) {
            oled.drawFastVLine(i + x, y, 5, SSD1306_WHITE);
            // draw lables
            oled.setTextSize(1);
            oled.setTextColor(SSD1306_WHITE, SSD1306_BLACK);
            oled.setCursor(i + x, y + 10);
            // addling a small value to eliminate round off errors
            // this val may need to be adjusted
            data = (i * (inc / stepval)) + loval + 0.00001;
            oled.print(data, dig);
        }
    }
    level = (w * (((curval - loval) / (hival - loval))));
    oled.fillRect(x + level, y - h, w - level, h, SSD1306_BLACK);
    oled.drawRect(x, y - h, w, h, SSD1306_WHITE);
    oled.fillRect(x, y - h, level, h, SSD1306_WHITE);
    // up until now print sends data to a video buffer NOT the screen
    // this call sends the data to the screen
}

void setup() {
    Serial.begin(9600);  // Serial Monitor

    /* Setup OLED */
    if (!oled.begin(SSD1306_SWITCHCAPVCC, 0x3C)) {          // initialize OLED display with I2C address 0x3C
        Serial.println(F("failed to start SSD1306 OLED"));  // Handler
    }

    oled.clearDisplay();                              // clear display
    oled.setTextColor(SSD1306_WHITE, SSD1306_BLACK);  // set text color
    drawCenterString("VI-ROSE", 31, 2);               // draw text center
    oled.display();
    delay(2000);

    /* Setup Servo */
    servo.attach(servoPin, 500, 2400);

    /* Setup Button */
    pinMode(GREEN_BUTTON_PIN, INPUT_PULLUP);  // set ESP32 pin to input pull-up mode
    pinMode(RED_BUTTON_PIN, INPUT_PULLUP);    // set ESP32 pin to input pull-up mode
    pinMode(BLUE_BUTTON_PIN, INPUT_PULLUP);   // set ESP32 pin to input pull-up mode

    /* Setup LED */
    pinMode(GREEN_LED_PIN, OUTPUT);  // set ESP32 pin to output mode
    pinMode(RED_LED_PIN, OUTPUT);    // set ESP32 pin to output mode

    /* Setup RTC CLock */
    if (!rtc.begin()) {
        Serial.println("RTC TIDAK TERBACA");
    }
    if (!rtc.isrunning()) {
        Serial.println("RTC is NOT running!");
        rtc.adjust(DateTime(F(_DATE), F(TIME_)));  // update rtc dari waktu komputer
    }

    /* Setup DHT Sensor */
    dht.setup(DHT_PIN, DHTesp::DHT22);

    /* Setup LDR */
    pinMode(LDR_PIN, INPUT);

    // Start Animation
    for (int16_t count = 0; count < 2; count++) {
        for (const auto &frame : framesStartAni) {
            oled.clearDisplay();
            oled.drawBitmap(32, 0, frame, FRAME_ANI_WIDTH, FRAME_ANI_HEIGHT, 1);
            oled.display();
        }
    }
}

void backCheck() {
    if (blue_btn_state != blue_prev_btn_state) {
        if (blue_btn_state == LOW) {
            backPrevTimer = currentMillis;
            blue_prev_btn_state = blue_btn_state;
        } else {
            backInterval = currentMillis - backPrevTimer;
            blue_prev_btn_state = blue_btn_state;
        }

        if (backInterval >= 3000) currScreen = 1;
    }
}

void backCheckAlarm() {
    if (blue_btn_state != blue_prev_btn_state) {
        if (blue_btn_state == LOW) {
            backPrevTimer = currentMillis;
            blue_prev_btn_state = blue_btn_state;
        } else {
            backInterval = currentMillis - backPrevTimer;
            blue_prev_btn_state = blue_btn_state;
        }

        if (backInterval >= 3000) {
            alarmOn = false;
            currScreen = 1;
        }
    }
}

void modeDHT() {
    backCheck();

    /* DHT Sensor */
    TempAndHumidity data = dht.getTempAndHumidity();
    String temperatureString = "Temp: " + String(data.temperature, 2) + "C\n";
    String humidityString = "Humidity: " + String(data.humidity, 1) + "%\n";
    String status;
    if (data.temperature > 40) {
        status = "Panas";
    } else if (data.temperature < 20) {
        status = "Dingin";
    } else if (data.temperature >= 20 && data.temperature <= 30) {
        status = "Normal";
    } else if (data.temperature > 30 && data.temperature < 40) {
        status = "Hangat";
    }

    // Servo
    pos = data.temperature + 40;
    servo.write(pos);

    // LED
    if (data.temperature < 20) {
        digitalWrite(GREEN_LED_PIN, HIGH);
        digitalWrite(RED_LED_PIN, LOW);
    } else if (data.temperature >= 20) {
        digitalWrite(RED_LED_PIN, HIGH);
        digitalWrite(GREEN_LED_PIN, LOW);
    }

    oled.clearDisplay();
    drawBarChartH(redrawGraph, data.temperature);
    drawCenterString(temperatureString, 2, 1);
    drawCenterString(humidityString, 12, 1);
    drawCenterString(status, 22, 1);
    oled.display();
}

void modeLDR() {
    backCheck();

    /* LDR */
    int analogValue = analogRead(LDR_PIN);
    float voltage = analogValue / 1024. * 5;
    float resistance = 2000 * voltage / (1 - voltage / 5);
    float lux = pow(RL10 * 1e3 * pow(10, GAMMA) / resistance, (1 / GAMMA));
    String ldrString = "LDR: " + String(lux, 2) + " lux\n";
    String status;
    if (lux < 150) {
        status = "Gelap";
    } else if (lux >= 150) {
        status = "Terang";
    }
    // Servo
    if (lux >= 6730.0) {
        pos = 130;
    } else {
        pos = 70;
    }
    servo.write(pos);

    // LED
    if (lux < 150) {
        digitalWrite(GREEN_LED_PIN, HIGH);
        digitalWrite(RED_LED_PIN, LOW);
    } else if (lux >= 150) {
        digitalWrite(RED_LED_PIN, HIGH);
        digitalWrite(RED_LED_PIN, LOW);
    }

    oled.clearDisplay();
    drawCenterString(ldrString, 31, 1);
    drawCenterString(status, 41, 1);
    oled.display();
}

void modeAlarm() {
    backCheck();

    if (green_btn_state == LOW) {
        switch (setParts) {
            case 0:
                hours++;
                break;
            case 1:
                minutes++;
                break;
            case 2:
                seconds++;
                break;
        }
    } else if (red_btn_state == LOW) {
        switch (setParts) {
            case 0:
                hours--;
                break;
            case 1:
                minutes--;
                break;
            case 2:
                seconds--;
                break;
        }
    } else if (blue_btn_state == LOW) {
        setParts++;
        if (setParts > 2) {
            alarmLong = (hours * 3600) + (minutes * 60) + seconds;
            alarmLong *= 1000;
            setParts = 0;
            alarmTimer = currentMillis;
            alarmOn = true;
            currScreen = 1;
        }
    }

    String setAlarm = String(hours) + ':' + String(minutes) + ':' + String(seconds);

    oled.clearDisplay();
    drawCenterString(setAlarm, 27, 2);
    oled.display();
}

void alarmScreen() {
    backCheckAlarm();

    oled.clearDisplay();
    oled.drawBitmap(53, 20, icons[2], 13, 10, 1);
    drawCenterString("ALARM!!", 40, 2);
    oled.display();
}

