void modes() {
    if (green_btn_state == LOW) {
        itemSelected--;
        if (itemSelected < 0) itemSelected = icons_LEN - 1;
    }
    if (red_btn_state == LOW) {
        itemSelected++;
        if (itemSelected > icons_LEN - 1) itemSelected = 0;
    }
    if ((blue_btn_state == LOW) && (blue_btn_clicked == 0)) {
        switch (itemSelected) {
            case 0:
                currScreen = 2;
                blue_btn_clicked = 1;
                break;
            case 1:
                currScreen = 3;
                blue_btn_clicked = 1;
                break;
            case 2:
                currScreen = 4;
                blue_btn_clicked = 1;
                break;
            case 3:
                currScreen = 5;
                blue_btn_clicked = 1;
                break;
        }
    }

    if ((blue_btn_state != LOW) && (blue_btn_clicked == 1)) {
        blue_btn_clicked = 0;
    }

    oled.clearDisplay();
    // DHT
    oled.drawBitmap(8, 2, icons[0], 13, 10, WHITE);
    drawString(modes_text[0], 32, 3);
    // LDR
    oled.drawBitmap(8, 18, icons[1], 13, 10, WHITE);
    drawString(modes_text[1], 32, 19);
    // Alarm
    oled.drawBitmap(8, 34, icons[2], 13, 10, WHITE);
    drawString(modes_text[2], 32, 35);
    // Special
    oled.drawBitmap(8, 50, icons[3], 13, 10, WHITE);
    drawString(modes_text[3], 32, 51);

    oled.drawBitmap(selectPos[itemSelected].first, selectPos[itemSelected].second, selected, 128, 15, WHITE);
    oled.display();
}

void loop() {
    currentMillis = millis();
    oled.setTextColor(SSD1306_WHITE, SSD1306_BLACK);

    /* Control Button */
    green_btn_state = digitalRead(GREEN_BUTTON_PIN);  // read new state
    red_btn_state = digitalRead(RED_BUTTON_PIN);      // read new state
    blue_btn_state = digitalRead(BLUE_BUTTON_PIN);    // read new state

    /* RTC Clock */
    DateTime now = rtc.now();
    String tanggal = String(namaHari[now.dayOfTheWeek()]) + ',' + String(now.day()) + '/' + String(now.month()) + '/' + String(now.year());
    String waktu = String(now.hour()) + ':' + String(now.minute()) + ':' + String(now.second());

    if (alarmOn == true) {
        if (currentMillis - alarmTimer >= alarmLong) {
            alarmTimer = currentMillis;
            currScreen = 6;
            alarmOn = false;
        }
    }

    if (blue_btn_state == LOW || red_btn_state == LOW || green_btn_state == LOW) {
        idleTimer = currentMillis;
    }

    if (currScreen == 0) {
        if (blue_btn_state == LOW || red_btn_state == LOW || green_btn_state == LOW) {
            currScreen = 1;
            idleTimer = currentMillis;
        }
        oled.clearDisplay();
        drawCenterString(waktu, 35, 1);
        drawCenterString(tanggal, 25, 1);
        oled.display();
    } else if (currScreen == 1) {
        modes();
        if (currentMillis - idleTimer >= idlePeriod) {
            currScreen = 0;
        }
    } else if (currScreen == 2) {
        modeDHT();
        if (currentMillis - idleTimer >= idlePeriod) {
            currScreen = 0;
        }
    } else if (currScreen == 3) {
        modeLDR();
        if (currentMillis - idleTimer >= idlePeriod) {
            currScreen = 0;
        }
    } else if (currScreen == 4) {
        modeAlarm();
        if (currentMillis - idleTimer >= idlePeriod) {
            currScreen = 0;
        }
    } else if (currScreen == 5) {
        modeSpecial();
        if (currentMillis - idleTimer >= idlePeriod) {
            currScreen = 0;
        }
    } else if (currScreen == 6) {
        alarmScreen();
        if (currentMillis - alarmTimer >= alarmPeriod) {
            currScreen = 0;
        }
    }
}